import React, { Component } from 'react';


class Habits extends Component {
  state = {
    habits: [
      {id: 0, name : 'Reading', count: 0 },
      {id: 1, name : 'Jogging', count: 0 },
      {id: 2, name : 'Studying', count: 0 }
    ]
  };
  render() {
    return (
      <ul>
        {
          this.state.habits.map(habit => (
            <Habit key={habit.id} habit={habit} onIncrement={this.handleIncrement} onDecrement={this.handleDecrement} onDelete={this.handleDelete}></Habit>
          ))}   
      </ul>
    );
  }
}

export default Habits;
